select * from BankFinanceDataselect count(id) as Total_Loan_Application from BankFinanceDataselect count(id) as Total_Loan_Application from BankFinanceData where month(issue_date)= 12 and year(issue_date)= 2021;select sum(loan_amount) as Total_Loan_Amount from BankFinanceDataSELECT SUM(loan_amount) AS Total_Loan_Amount FROM BankFinanceData WHERE MONTH(issue_date)= 11 and YEAR(issue_date)= 2021;select sum(total_payment) as MTD_Total_Payment from BankFinanceData WHERE MONTH(issue_date)= 12 and YEAR(issue_date)= 2021;select sum(total_payment) as PrevMTD_Total_Payment from BankFinanceData WHERE MONTH(issue_date)= 11 and YEAR(issue_date)= 2021;select avg(int_rate)* 100 as MTD_Avg_interest_rate from BankFinanceData WHERE MONTH(issue_date)= 12 and YEAR(issue_date)= 2021;select avg(int_rate)* 100 as PrevMTM_Avg_interest_rate from BankFinanceData WHERE MONTH(issue_date)= 11 and YEAR(issue_date)= 2021;select avg(dti)* 100 as MTD_Avg_dti_rate from BankFinanceDataselect avg(dti)* 100 as MTD_Avg_dti_rate from BankFinanceData WHERE MONTH(issue_date)= 12 and YEAR(issue_date)= 2021;select avg(dti)* 100 as PrevMTM_Avg_dti_rate from BankFinanceData WHERE MONTH(issue_date)= 11 and YEAR(issue_date)= 2021;/* select * from BankFinanceData */select 	(COUNT(CASE WHEN loan_status = 'Charged Off' then id end)* 100)	/	COUNT(id) as bad_loan_perfrom BankFinanceDataselect count(id) as bad_loan_application from BankFinanceData where loan_status = 'Charged Off'select sum(loan_amount) as bad_loan_fund_amt from BankFinanceData where loan_status = 'Charged Off'select sum(total_payment) as bad_loan_total_rec_amt from BankFinanceData where loan_status = 'Charged Off'select 	(COUNT(CASE WHEN loan_status = 'Fully Paid' or loan_status= 'Current' then id end)* 100)	/	COUNT(id) as good_loan_perfrom BankFinanceDataselect count(id) as good_loan_application from BankFinanceData where loan_status='Fully Paid' or loan_status='Current'select sum(loan_amount) as good_loan_fund_amt from BankFinanceData where loan_status='Fully Paid' or loan_status='Current'select sum(total_payment) as good_loan_total_rec_amt from BankFinanceData where loan_status='Fully Paid' or loan_status='Current'/*LOAN STATUS*/SELECT    loan_status,    COUNT(id) AS LoanCount,    SUM(total_payment) AS Total_Amount_Received,    SUM(loan_amount) AS Total_Funded_Amount,    AVG(int_rate * 100) AS Interest_Rate,    AVG(dti * 100) AS DTIFROM BankFinanceDataGROUP BY loan_statusSELECT 	loan_status, 	SUM(total_payment) AS MTD_Total_Amount_Received, 	SUM(loan_amount) AS MTD_Total_Funded_Amount FROM BankFinanceDataWHERE MONTH(issue_date) = 12 GROUP BY loan_status/*BANK LOAN REPORT | OVERVIEWMONTH*/SELECT 	MONTH(issue_date) AS Month_Munber, 	DATENAME(MONTH, issue_date) AS Month_name, 	COUNT(id) AS Total_Loan_Applications,	SUM(loan_amount) AS Total_Funded_Amount,	SUM(total_payment) AS Total_Amount_ReceivedFROM BankFinanceDataGROUP BY MONTH(issue_date), DATENAME(MONTH, issue_date)ORDER BY MONTH(issue_date)/*STATE */SELECT 	address_state AS State, 	COUNT(id) AS Total_Loan_Applications,	SUM(loan_amount) AS Total_Funded_Amount,	SUM(total_payment) AS Total_Amount_ReceivedFROM BankFinanceDataGROUP BY address_stateORDER BY address_state/*TERM */SELECT 	term AS Term, 	COUNT(id) AS Total_Loan_Applications,	SUM(loan_amount) AS Total_Funded_Amount,	SUM(total_payment) AS Total_Amount_ReceivedFROM BankFinanceDataGROUP BY termORDER BY term/*EMPLOYEE LENGTH */SELECT 	emp_length AS Employee_Length, 	COUNT(id) AS Total_Loan_Applications,	SUM(loan_amount) AS Total_Funded_Amount,	SUM(total_payment) AS Total_Amount_ReceivedFROM BankFinanceDataGROUP BY emp_lengthORDER BY emp_length/* PURPOSE */SELECT 	purpose AS PURPOSE, 	COUNT(id) AS Total_Loan_Applications,	SUM(loan_amount) AS Total_Funded_Amount,	SUM(total_payment) AS Total_Amount_ReceivedFROM BankFinanceDataGROUP BY purposeORDER BY purpose/* HOME OWNERSHIP */SELECT 	home_ownership AS Home_Ownership, 	COUNT(id) AS Total_Loan_Applications,	SUM(loan_amount) AS Total_Funded_Amount,	SUM(total_payment) AS Total_Amount_ReceivedFROM BankFinanceDataGROUP BY home_ownershipORDER BY home_ownershipSELECT 	purpose AS PURPOSE, 	COUNT(id) AS Total_Loan_Applications,	SUM(loan_amount) AS Total_Funded_Amount,	SUM(total_payment) AS Total_Amount_ReceivedFROM BankFinanceDataWHERE grade = 'A'GROUP BY purposeORDER BY purpose